import { useReducer } from "react"
import CartContext from "./cart-context"
const defaultCartReducer = {
    items: [],
    totalAmount: 0
}

const cartReducer = (state, actions) => {

    if (actions.type === 'ADD') {
        const updatedItems = state.items.concat(actions.item)
        const updatedTotalAmount = state.totalAmount + actions.item.price * actions.item.amount
        return {
            updatedItems,
            updatedTotalAmount
        }

    }
    if (actions.type === 'REMOVE') { state = actions.id }
    return defaultCartReducer
}
const CartProvider = props => {
    const [cartState, dispatchCartAction] = useReducer(cartReducer, defaultCartReducer);


    const addItemToCartHandler = item => {
        dispatchCartAction({ type: 'ADD', item: item })
    }

    const removeItemFromCartHandler = id => {
        dispatchCartAction({ type: 'REMOVE', id: id })
    }

    const cartContext = {
        items: cartState.items,
        totalAmount: cartState.totalAmount,
        addItem: addItemToCartHandler,
        removeItem: removeItemFromCartHandler

    }



    return <CartContext.Provider value={cartContext}>
        {props.children}
    </CartContext.Provider>

}

export default CartProvider
