import React, { useContext, useCallback, useEffect } from 'react';

import Modal from '../UI/Modal';
import CartItem from './CartItem';
import classes from './Cart.module.css';
import CartContext from '../../store/cart-context';
import Checkout from './Checkout';
import { useSelector, useDispatch } from 'react-redux';
import { orderActions } from '../../store/SendOrderResponses'


const Cart = (props) => {
  const cartCtx = useContext(CartContext);

  const dispatch = useDispatch()

  const { isCheckout, isSubmitting, didSubmit, error } = useSelector(state => state.orders)

  const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`;
  const hasItems = cartCtx.items.length > 0;

  const cartItemRemoveHandler = (id) => {
    cartCtx.removeItem(id);
  };

  const cartItemAddHandler = (item) => {
    cartCtx.addItem(item);
  };

  const orderHandler = () => {
    dispatch(orderActions.setIsCheckout(true))
  };

  // useEffect(() => {
  //   if (error !== '') {
  //     cartCtx.clearCart()
  //     console.log('error')
  //   }

  //   return () => { }
  // }, [cartCtx, error])

  const submitOrderHandler = useCallback(async (userData) => {
    dispatch(orderActions.sendOrderRequest({ userData, items: cartCtx.items }))
    cartCtx.clearCart();
  }, [cartCtx, dispatch])

  const cartItems = (
    <ul className={classes['cart-items']}>
      {cartCtx.items.map((item) => (
        <CartItem
          key={item.id}
          name={item.name}
          amount={item.amount}
          price={item.price}
          onRemove={cartItemRemoveHandler.bind(null, item.id)}
          onAdd={cartItemAddHandler.bind(null, item)}
        />
      ))}
    </ul>
  );

  const modalActions = (
    <div className={classes.actions}>
      <button className={classes['button--alt']} onClick={props.onClose}>
        Close
      </button>
      {hasItems && (
        <button className={classes.button} onClick={orderHandler}>
          Order
        </button>
      )}
    </div>
  );

  const cartModalContent = (
    <React.Fragment>
      {cartItems}
      <div className={classes.total}>
        <span>Total Amount</span>
        <span>{totalAmount}</span>
      </div>
      {isCheckout && (
        <Checkout onConfirm={submitOrderHandler} onCancel={props.onClose} />
      )}
      {!isCheckout && modalActions}
    </React.Fragment>
  );

  const isSubmittingModalContent = <p>Sending order data...</p>


  const orderState = error ? 'Sending order Failed!' : 'Successfully sent the order!'

  const didSubmitModalContent = (
    <React.Fragment>
      <p>{orderState}</p>
      <div className={classes.actions}>
        <button className={classes.button} onClick={props.onClose}>
          Close
      </button>
      </div>
    </React.Fragment>
  );

  return (
    <Modal onClose={props.onClose}>
      {!isSubmitting && !didSubmit && cartModalContent}
      {!error && isSubmitting && isSubmittingModalContent}
      {!isSubmitting && didSubmit && didSubmitModalContent}
    </Modal>
  );
};

export default Cart;
